DROP TABLE IF EXISTS `t_comment`;
CREATE TABLE `t_comment`  (
  `id` bigint UNSIGNED NOT NULL COMMENT '主键',
  `user_id` bigint UNSIGNED NOT NULL COMMENT '用户ID',
  `source_id` bigint UNSIGNED NOT NULL COMMENT '源id（该id和评论id相同时为树洞评论）',
