DROP TABLE IF EXISTS `t_label`;
CREATE TABLE `t_label`  (
  `id` bigint UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键',
  `classify_id` bigint UNSIGNED NOT NULL COMMENT '类别ID',
  `name` varchar(16) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '标签名',
  `cover` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '封面地址',
  `introduction` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '简介',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  `create_by` bigint UNSIGNED NOT NULL COMMENT '创建人',
  `update_by` bigint UNSIGNED NOT NULL COMMENT '修改人',
  `is_enabled` tinyint UNSIGNED NOT NULL DEFAULT 1 COMMENT '是否启用',
  `is_reviewed` tinyint UNSIGNED NOT NULL DEFAULT 2 COMMENT '是否通过审核',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `t_label_classify_id_index`(`classify_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 50000100002 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '标签表' ROW_FORMAT = DYNAMIC;

