INSERT INTO `t_label` VALUES (10000100002, 100001, '背景', '/files/20250327/20250327174923589.webp', '随机背景', '2025-05-01 22:07:33', '2025-06-11 10:45:10', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000100001, 200001, '我们生活在巨大的差距里', '/files/20250327/20250327211958412.webp', '这就是我们今天的生活，不平衡的生活。 区域之间的不平衡、经济发展的不平衡，个人生活的不平衡等等，然后就是心理的不平衡，最后连梦想都不平衡了。', '2025-05-01 22:07:33', '2025-06-11 10:48:13', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000100002, 200001, '说走就走', '/files/20250405/20250405201143311.webp', '去哪儿不重要，重要的是去！', '2025-05-01 22:07:33', '2025-06-11 10:48:35', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000100003, 200001, '待到重阳日\r\n', '/files/20250327/20250327215042845.webp', '改天是哪天，下次是那次，以后是多久，去经历，去后悔，去热爱，去记录，去感受，去奔赴山海。', '2025-05-01 22:07:33', '2025-06-11 10:49:27', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200001, 200002, 'TypeScript', '/files/20250327/20250327173239381.webp', 'TypeScript 是 JavaScript 的超集，扩展了 JavaScript 的语法，TypeScript 通过类型注解提供编译时的静态类型检查。', '2025-05-01 22:07:33', '2025-06-11 10:49:51', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200002, 200002, 'Redis', '/files/20250327/20250327173718255.webp', 'Redis是一个简单、高效、分布式、基于内存的缓存工具，通过网络连接提供Key-Value式的缓存服务。', '2025-05-01 22:07:33', '2025-06-11 10:50:06', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200003, 200002, 'Git', '/files/20250327/20250327174930092.webp', 'Git 是一个开源的分布式版本控制系统，用于敏捷高效地处理任何或小或大的项目。', '2025-05-01 22:07:33', '2025-06-11 10:50:15', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200004, 200002, 'JWT', '/files/20250327/20250327174923589.webp', 'JSON Web Token (JWT)是一个开放标准(RFC 7519) ，用于作为 JSON 对象在各方之间安全地传输信息。', '2025-05-01 22:07:33', '2025-06-11 10:50:25', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200005, 200002, 'MongDB', '/files/20250327/20250327175033911.webp', 'MongoDB 是一个基于分布式文件存储的数据库。旨在为 WEB 应用提供可扩展的高性能数据存储解决方案。', '2025-05-01 22:07:33', '2025-06-11 10:50:39', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200006, 200002, 'K8S', '/files/20250327/20250327180342799.webp', 'Kubernetes 是一个开源的容器编排引擎，用来对容器化应用进行自动化部署、 扩缩和管理。', '2025-05-01 22:07:33', '2025-06-11 10:51:04', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200007, 200002, 'HTML', '/files/20250327/20250327205217775.webp', 'HTML 是一种用于创建网页的标准标记语言。您可以使用 HTML 来建立自己的 WEB 站点，HTML 运行在浏览器上，由浏览器来解析。', '2025-05-01 22:07:33', '2025-06-11 10:51:25', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200008, 200002, 'Nginx', '/files/20250327/20250327205506421.webp', 'Nginx是一款轻量级的Web服务器、反向代理服务器，由于它的内存占用少，启动极快，高并发能力强，在互联网项目中广泛应用。', '2025-05-01 22:07:33', '2025-06-11 10:51:39', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200009, 200002, 'Java', '/files/20250327/20250327204833525.webp', 'Java 是由 Sun Microsystems 公司于 1995 年 5 月推出的 Java 面向对象程序设计语言和 Java 平台的总称。', '2025-05-01 22:07:33', '2025-06-11 10:51:49', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200010, 200002, 'Python', '/files/20250327/20250327205020321.webp', 'Python 是一种解释型、面向对象、动态数据类型的高级程序设计语言。由 Guido van Rossum 于 1989 年底发明，第一个公开发行版发行于 1991 年。', '2025-05-01 22:07:33', '2025-06-11 10:52:00', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200011, 200002, 'Shiro', '/files/20250327/20250327205212718.webp', 'Apache Shiro™是一个强大且易用的Java安全框架，能够用于身份验证、授权、加密和会话管理。', '2025-05-01 22:07:33', '2025-06-11 10:52:11', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200012, 200002, 'Vue', '/files/20250327/20250327205559028.webp', 'Vue 是一款用于构建用户界面的 JavaScript 框架。它基于标准 HTML、CSS 和 JavaScript 构建，并提供了一套声明式的、组件化的编程模型，帮助你高效地开发用户界面。', '2025-05-01 22:07:33', '2025-06-11 10:52:22', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200013, 200002, 'MySQL', '/files/20250327/20250327205703868.webp', 'MySQL 是最流行的关系型数据库管理系统，在 WEB 应用方面 MySQL 是最好的 RDBMS 应用软件之一。', '2025-05-01 22:07:33', '2025-06-11 10:52:43', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200014, 200002, 'Axios', '/files/20250327/20250327205836110.webp', 'Axios 是一个基于 promise 网络请求库，作用于node.js 和浏览器中。', '2025-05-01 22:07:33', '2025-06-11 10:52:55', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200015, 200002, 'MyBatis', '/files/20250327/20250327205838157.webp', 'MyBatis 是一流的持久性框架，支持自定义 SQL、存储过程和高级映射。MyBatis 消除了几乎所有的 JDBC 代码、手动参数设置和结果检索。', '2025-05-01 22:07:33', '2025-06-11 10:53:24', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200016, 200002, 'Docker', '/files/20250327/20250327205840068.webp', 'Docker 是一个开源的应用容器引擎，可以让开发者打包他们的应用以及依赖包到一个轻量级、可移植的容器中，然后发布到任何流行的 Linux 机器上，也可以实现虚拟化。', '2025-05-01 22:07:33', '2025-06-11 10:53:36', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200017, 200002, 'Spring 系列', '/files/20250327/20250327210938769.webp', '低侵入式设计，代码的污染极低，Spring的IoC容器降低了业务对象替换的复杂性，提高了组件之间的解耦。AOP支持允许将一些通用任务如安全、事务、日志等进行集中式管理，从而提供了更好的复用。', '2025-05-01 22:07:33', '2025-06-11 10:54:01', 999999999999999, 999999999999999, 1, 1);
INSERT INTO `t_label` VALUES (20000200018, 200002, 'CSS', '/files/20250327/20250327210124642.webp', 'CSS（层叠样式表）用于设置网页的样式及布局。', '2025-06-11 01:03:35', '2025-06-11 10:54:34', 999999999999999, 999999999999999, 1, 2);
