DROP TABLE IF EXISTS `t_router`;
CREATE TABLE `t_router`  (
  `id` bigint UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键（自增）',
  `role_id` bigint UNSIGNED NOT NULL COMMENT '权限ID 向下兼容',
  `parent_id` bigint UNSIGNED NOT NULL DEFAULT 0 COMMENT '父路由ID',
  `name` varchar(16) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '前端通过name获取路由',
  `title` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '路由标题',
  `sort` tinyint UNSIGNED NOT NULL DEFAULT 1 COMMENT '路由排序',
  `path` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '路由地址',
  `icon` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '路由前缀图标',
  `component` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '路由组件路径',
  `type` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '路由类型（B 按钮，M 菜单，O 导航栏不显示路由）',
  `is_admin` tinyint UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否是admin端路由（0 ui, 1 admin）',
  `is_frame` tinyint UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否外链',
  `is_enabled` tinyint UNSIGNED NOT NULL DEFAULT 1 COMMENT '是否启用',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `t_router_parent_id_priority_index`(`parent_id` ASC, `sort` ASC) USING BTREE,
  INDEX `t_router_role_id_index`(`role_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1001 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '路由表' ROW_FORMAT = DYNAMIC;

